import api from './axiosConfig.js';

export default {
  // Auth
  register: async (username, password) => {
    const result = await api.post('/register', { username, passwordHash: password });
    return result.data;
}
,

  login: async (username, password) => {
    api.post('/login', { username, password });

    const token = result.data.token;
    localStorage.setItem('jwt', token); // שומר את ה-JWT
    return token;
  },

  logout: () => {
    localStorage.removeItem('jwt');
  },

  // Tasks
  getTasks: async () => {
    const result = await api.get('/tasks');
    return result.data;
  },

  addTask: async (name) => {
    const newTask = { name, isComplete: false };
    const result = await api.post('/tasks', newTask);
    return result.data;
  },

  setCompleted: async (id, isComplete) => {
    const result = await api.put(`/tasks/${id}`, { id, isComplete });
    return result.data;
  },

  deleteTask: async (id) => {
    await api.delete(`/tasks/${id}`);
  }
};
