import React, { useState, useEffect } from "react";
import service from "./service.js";

function App() {
  const [userLoggedIn, setUserLoggedIn] = useState(false);
  const [currentScreen, setCurrentScreen] = useState("login"); // "login" | "register" | "todos"
  const [errorMessage, setErrorMessage] = useState("");

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");

  // ===================== AUTH =====================
  const handleLogin = async (e) => {
    e.preventDefault();
    setErrorMessage("");
    try {
      await service.login(username, password);
      setUserLoggedIn(true);
      setCurrentScreen("todos");
    } catch {
      setErrorMessage("שם משתמש או סיסמה שגויים");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setErrorMessage("");
    try {
      await service.register(username, password);
      setErrorMessage("הרשמה הצליחה! התחברי עכשיו");
      setCurrentScreen("login");
      setUsername("");
      setPassword("");
    } catch {
      setErrorMessage("הרשמה נכשלה, נסי שם אחר");
    }
  };

  const handleLogout = () => {
    service.logout();
    setUserLoggedIn(false);
    setCurrentScreen("login");
    setUsername("");
    setPassword("");
    setTodos([]);
  };

  // ===================== TODOS =====================
  const getTodos = async () => {
    try {
      const data = await service.getTasks();
      setTodos(data);
    } catch (err) {
      console.log(err);
    }
  };

  const addTodo = async (e) => {
    e.preventDefault();
    if (!newTodo) return;
    await service.addTask(newTodo);
    setNewTodo("");
    getTodos();
  };

  const toggleComplete = async (todo) => {
    await service.setCompleted(todo.id, !todo.isComplete);
    getTodos();
  };

  const deleteTodo = async (id) => {
    await service.deleteTask(id);
    getTodos();
  };

  useEffect(() => {
    if (currentScreen === "todos") getTodos();
  }, [currentScreen]);

  // ===================== RENDER =====================
  if (currentScreen === "login") {
    return (
      <div>
        <h2>Login</h2>
        {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
        <form onSubmit={handleLogin}>
          <input type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} required />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
          <button type="submit">Login</button>
        </form>
        <p>אין לך חשבון? <button onClick={() => { setCurrentScreen("register"); setErrorMessage(""); }}>הרשמה</button></p>
      </div>
    );
  }

  if (currentScreen === "register") {
    return (
      <div>
        <h2>Register</h2>
        {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
        <form onSubmit={handleRegister}>
          <input type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} required />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
          <button type="submit">Register</button>
        </form>
        <p>כבר יש לך חשבון? <button onClick={() => { setCurrentScreen("login"); setErrorMessage(""); }}>Login</button></p>
      </div>
    );
  }

  // ===================== TODOS SCREEN =====================
  return (
    <div>
      <h2>ToDos</h2>
      <button onClick={handleLogout}>Logout</button>
      <form onSubmit={addTodo}>
        <input type="text" placeholder="New Task" value={newTodo} onChange={e => setNewTodo(e.target.value)} />
        <button type="submit">Add</button>
      </form>
      <ul>
        {todos.map(todo => (
          <li key={todo.id}>
            <input type="checkbox" checked={todo.isComplete} onChange={() => toggleComplete(todo)} />
            {todo.name}
            <button onClick={() => deleteTodo(todo.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
