import axios from 'axios';

// מגדירים את הכתובת כברירת מחדל לכל הקריאות
axios.defaults.baseURL = 'http://localhost:3000';

// מוסיפים interceptor לשגיאות
axios.interceptors.response.use(
  response => response,
  error => {
    console.error('שגיאה מהשרת:', error);
    return Promise.reject(error);
  }
);

export default {
  // שליפת משימות
  getTasks: async () => {
    const result = await axios.get('/tasks');
    return result.data;
  },

  // הוספת משימה
  addTask: async (name) => {
    const newTask = { name, isComplete: false };
    const result = await axios.post('/tasks', newTask);
    return result.data;
  },

  // עדכון משימה
  setCompleted: async (id, isComplete) => {
    const result = await axios.put(`/tasks/${id}`, { id, isComplete });
    return result.data;
  },

  // מחיקת משימה
  deleteTask: async (id) => {
    await axios.delete(`/tasks/${id}`);
  }
};
