import axios from 'axios';

// URL של ה-API - FIXED FOR RENDER
const API_URL = 'https://todoapis-qdh6.onrender.com';

// פונקציה עזר ליצירת config עם JWT
const getConfig = () => {
  const token = localStorage.getItem('jwt');
  const config = {
    headers: {
      'Content-Type': 'application/json'
    }
  };
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
};

// פונקציה עזר לטיפול בשגיאות
const handleError = (error) => {
  console.error('API Error:', error.response?.data || error.message);
  if (error.response && error.response.status === 401) {
    localStorage.removeItem('jwt');
    if (window.location.pathname !== '/') {
      window.location.href = '/';
    }
  }
  throw error;
};


export default {
  // =====================
  // Auth
  // =====================
  register: async (username, password) => {
    const result = await apiClient.post('https://todoapis-qdh6.onrender.com/register', { username, passwordHash: password });
    return result.data;
  },

  login: async (username, password) => {
    const result = await apiClient.post('https://todoapis-qdh6.onrender.com/login', { username, password });
    const token = result.data.token;
    localStorage.setItem('jwt', token); // שמירת JWT ב-localStorage
    return token;
  },

  logout: () => {
    localStorage.removeItem('jwt');
  },

  // =====================
  // Tasks
  // =====================
  getTasks: async () => {
    const result = await apiClient.get('https://todoapis-qdh6.onrender.com/tasks');
    return result.data;
  },


  addTask: async (name) => {
    const result = await apiClient.post('https://todoapis-qdh6.onrender.com/tasks', { name, isComplete: false });
    return result.data;
  },

  setCompleted: async (id, name, isComplete) => {
    const result = await apiClient.put(`https://todoapis-qdh6.onrender.com/tasks/${id}`, { id, name, isComplete });
    return result.data;
  },

  deleteTask: async (id) => {
    await apiClient.delete(`https://todoapis-qdh6.onrender.com/tasks/${id}`);
  }
};
