import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5098', // כתובת ה-API שלך
});

// interceptor להוספת JWT לכל בקשה
api.interceptors.request.use(config => {
  const token = localStorage.getItem('jwt'); // נשמר בלוקלסטורג'
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// interceptor לתפיסת 401 והפניה ללוגין
api.interceptors.response.use(
  response => response,
  error => {
    if (error.response && error.response.status === 401) {
      window.location.href = '/login'; // מפנה לדף לוגין
    }
    return Promise.reject(error);
  }
);

export default api;
