import React, { useState, useEffect } from "react";
import service from "./service.js";
import "./App.css";

function App() {
  const [userLoggedIn, setUserLoggedIn] = useState(false);
  const [currentScreen, setCurrentScreen] = useState("login"); // "login" | "register" | "todos"
  const [errorMessage, setErrorMessage] = useState("");

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");

  // ===================== AUTH =====================
  const handleLogin = async (e) => {
    e.preventDefault();
    setErrorMessage("");
    try {

      await service.login(username, password);
      setUserLoggedIn(true);
      setCurrentScreen("todos");
    } catch (error) {
      console.error("Login error:", error);
      setErrorMessage("◊©◊ù ◊û◊©◊™◊û◊© ◊ê◊ï ◊°◊ô◊°◊û◊î ◊©◊í◊ï◊ô◊ô◊ù: " + (error.response?.data || error.message));
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setErrorMessage("");
    try {
      await service.register(username, password);
      setErrorMessage("◊î◊®◊©◊û◊î ◊î◊¶◊ú◊ô◊ó◊î! ◊î◊™◊ó◊ë◊®◊ô ◊¢◊õ◊©◊ô◊ï");
      setCurrentScreen("login");
      setUsername("");
      setPassword("");
    } catch (error) {
      console.error("Register error:", error);
      setErrorMessage("◊î◊®◊©◊û◊î ◊†◊õ◊©◊ú◊î: " + (error.response?.data || error.message));
    }
  };

  const handleLogout = () => {
    service.logout();
    setUserLoggedIn(false);
    setCurrentScreen("login");
    setUsername("");
    setPassword("");
    setTodos([]);
  };

  // ===================== TODOS =====================
  const getTodos = async () => {
    try {
      const data = await service.getTasks();
      setTodos(data);
    } catch (err) {
      console.log(err);
    }
  };


  const addTodo = async (e) => {
    e.preventDefault();
    if (!newTodo) return;
    await service.addTask(newTodo);
    setNewTodo("");
    getTodos();
  };

  const toggleComplete = async (todo) => {
    await service.setCompleted(todo.id, todo.name, !todo.isComplete);
    getTodos();
  };

  const deleteTodo = async (id) => {
    await service.deleteTask(id);
    getTodos();
  };

  useEffect(() => {
    if (currentScreen === "todos") getTodos();
  }, [currentScreen]);

  // ===================== RENDER =====================
  if (currentScreen === "login") {
    return (
      <div className="app-container">
        <div className="auth-container">
          <h2>üîê ◊î◊™◊ó◊ë◊®◊ï◊™</h2>
          {errorMessage && <div className="error-message">{errorMessage}</div>}
          <form onSubmit={handleLogin} className="auth-form">
            <input 
              type="text" 
              placeholder="◊©◊ù ◊û◊©◊™◊û◊©" 
              value={username} 
              onChange={e => setUsername(e.target.value)} 
              required 
              className="form-input"
            />
            <input 
              type="password" 
              placeholder="◊°◊ô◊°◊û◊î" 
              value={password} 
              onChange={e => setPassword(e.target.value)} 
              required 
              className="form-input"
            />
            <button type="submit" className="btn-primary">◊î◊™◊ó◊ë◊®</button>
          </form>
          <div className="switch-auth">
            ◊ê◊ô◊ü ◊ú◊ö ◊ó◊©◊ë◊ï◊ü? 
            <button onClick={() => { setCurrentScreen("register"); setErrorMessage(""); }} className="btn-link">
              ◊î◊ô◊®◊©◊ù ◊õ◊ê◊ü
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (currentScreen === "register") {
    return (
      <div className="app-container">
        <div className="auth-container">
          <h2>üìù ◊î◊®◊©◊û◊î</h2>
          {errorMessage && (
            <div className={errorMessage.includes("◊î◊¶◊ú◊ô◊ó◊î") ? "success-message" : "error-message"}>
              {errorMessage}
            </div>
          )}
          <form onSubmit={handleRegister} className="auth-form">
            <input 
              type="text" 
              placeholder="◊©◊ù ◊û◊©◊™◊û◊©" 
              value={username} 
              onChange={e => setUsername(e.target.value)} 
              required 
              className="form-input"
            />
            <input 
              type="password" 
              placeholder="◊°◊ô◊°◊û◊î" 
              value={password} 
              onChange={e => setPassword(e.target.value)} 
              required 
              className="form-input"
            />
            <button type="submit" className="btn-primary">◊î◊ô◊®◊©◊ù</button>
          </form>
          <div className="switch-auth">
            ◊õ◊ë◊® ◊ô◊© ◊ú◊ö ◊ó◊©◊ë◊ï◊ü? 
            <button onClick={() => { setCurrentScreen("login"); setErrorMessage(""); }} className="btn-link">
              ◊î◊™◊ó◊ë◊® ◊õ◊ê◊ü
            </button>
          </div>
        </div>
      </div>
    );
  }

  // ===================== TODOS SCREEN =====================
  return (
    <div className="app-container">
      <div className="todos-container">
        <div className="todos-header">
          <h2>üìã ◊î◊û◊©◊ô◊û◊ï◊™ ◊©◊ú◊ô</h2>
          <button onClick={handleLogout} className="btn-logout">◊î◊™◊†◊™◊ß</button>
        </div>

        <form onSubmit={addTodo} className="add-todo-form">
          <input 
            type="text" 
            placeholder="◊î◊ï◊°◊£ ◊û◊©◊ô◊û◊î ◊ó◊ì◊©◊î..." 
            value={newTodo} 
            onChange={e => setNewTodo(e.target.value)} 
            className="add-todo-input"
          />
          <button type="submit" className="btn-add">‚ûï ◊î◊ï◊°◊£</button>
        </form>

        {todos.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">üìù</div>
            <div className="empty-state-text">◊ê◊ô◊ü ◊û◊©◊ô◊û◊ï◊™ ◊¢◊ì◊ô◊ô◊ü. ◊™◊™◊ó◊ô◊ú ◊ú◊î◊ï◊°◊ô◊£!</div>
          </div>
        ) : (
          <ul className="todos-list">
            {todos.map(todo => (
              <li key={todo.id} className="todo-item">
                <input 
                  type="checkbox" 
                  checked={todo.isComplete} 
                  onChange={() => toggleComplete(todo)} 
                  className="todo-checkbox"
                />
                <span className={`todo-text ${todo.isComplete ? 'completed' : ''}`}>
                  {todo.name}
                </span>
                <button onClick={() => deleteTodo(todo.id)} className="btn-delete">
                  üóëÔ∏è ◊û◊ó◊ß
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default App;
