import axios from 'axios';

// הגדרת כתובת ברירת מחדל לכל הקריאות ל-API
axios.defaults.baseURL = "http://localhost:5098"; // החליפי ל-port שלך אם שונה

// Interceptor שמאזין לכל תגובה מהשרת ותופס שגיאות
axios.interceptors.response.use(
  (response) => response, // אם הכול תקין – פשוט מחזיר את התגובה
  (error) => {
    console.error("❌ שגיאה ב-API:", error.message || error);
    return Promise.reject(error);
  }
);

// שירות API – כל הפונקציות שמתקשרות לשרת
const service = {

  getTasks: async () => {
    const result = await axios.get("/tasks"); // הנתיב חייב להתאים ל-controller שלך
    return result.data;
  },


  addTask: async (name) => {
    const newTask = { name, isComplete: false };
    const result = await axios.post("/tasks", newTask);
    return result.data;
  },

  setCompleted: async (id, isComplete) => {
    const result = await axios.put(`/tasks/${id}`, { id, isComplete });
    return result.data;
  },


  deleteTask: async (id) => {
    const result = await axios.delete(`/tasks/${id}`);
    return result.data;
  }
};

export default service;
