const API_BASE_URL = 'https://todoapis-qdh6.onrender.com';

// Debug - ◊î◊ì◊§◊°◊™ ◊î-API URL
console.log('üåê API CONFIG - REACT_APP_API_URL from env:', process.env.REACT_APP_API_URL);
console.log('üåê API CONFIG - API_BASE_URL (hardcoded):', API_BASE_URL);
console.log('üåê API CONFIG - API_BASE_URL type:', typeof API_BASE_URL);
console.log('üåê API CONFIG - API_BASE_URL length:', API_BASE_URL ? API_BASE_URL.length : 0);

// ◊§◊ï◊†◊ß◊¶◊ô◊î ◊¢◊ñ◊® ◊ú◊ô◊¶◊ô◊®◊™ config ◊¢◊ù JWT
const getConfig = () => {
  const token = localStorage.getItem('jwt');
  const config = {
    headers: {
      'Content-Type': 'application/json'
    }
  };
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
};

// ◊§◊ï◊†◊ß◊¶◊ô◊î ◊¢◊ñ◊® ◊ú◊ò◊ô◊§◊ï◊ú ◊ë◊©◊í◊ô◊ê◊ï◊™
const handleError = (error) => {
  console.error('API Error:', error.response?.data || error.message);
  if (error.response && error.response.status === 401) {
    localStorage.removeItem('jwt');
    if (window.location.pathname !== '/') {
      window.location.href = '/';
    }
  }
  throw error;
};


export default {
  // =====================
  // Auth
  // =====================
  register: async (username, password) => {
    try {
      // CRITICAL FIX: ◊©◊ô◊û◊ï◊© ◊ë-URL ◊ô◊©◊ô◊® ◊õ◊ì◊ô ◊ú◊î◊ë◊ò◊ô◊ó ◊©◊î◊ï◊ê ◊†◊ò◊û◊¢ ◊ë-build
      const url = API_BASE_URL + '/register';
      console.log('üîµ REGISTER - Sending request to:', url);
      console.log('üîµ REGISTER - API_BASE_URL:', API_BASE_URL);
      console.log('üîµ REGISTER - Username:', username);
      console.log('üîµ REGISTER - Payload:', { username, passwordHash: password });

      const result = await axios.post(url, { username, passwordHash: password }, getConfig());

      console.log('üü¢ REGISTER - Success! Response:', result.data);
      console.log('üü¢ REGISTER - Status:', result.status);

      return result.data;
    } catch (error) {
      console.error('üî¥ REGISTER - Error:', error);
      console.error('üî¥ REGISTER - Error response:', error.response?.data);
      console.error('üî¥ REGISTER - Error status:', error.response?.status);
      handleError(error);
    }
  },

  login: async (username, password) => {
    try {
      // CRITICAL FIX: ◊©◊ô◊û◊ï◊© ◊ë-URL ◊ô◊©◊ô◊® ◊õ◊ì◊ô ◊ú◊î◊ë◊ò◊ô◊ó ◊©◊î◊ï◊ê ◊†◊ò◊û◊¢ ◊ë-build
