      console.log('üîµ LOGIN - Payload:', { username, password: '***' });

      const result = await axios.post(fullUrl, { username, password }, getConfig());

      console.log('üü¢ LOGIN - Success! Status:', result.status);
      console.log('üü¢ LOGIN - Response headers:', result.headers);
      console.log('üü¢ LOGIN - Content-Type:', result.headers['content-type']);
