      console.log('üü¢ LOGIN - Token saved to localStorage');

      return token;
    } catch (error) {
      console.error('üî¥ LOGIN - Error:', error);
      console.error('üî¥ LOGIN - Error response:', error.response?.data);
      console.error('üî¥ LOGIN - Error status:', error.response?.status);
