import React, { useState } from 'react';
import Login from './pages/loginogin';
import Register from './pages/Register';
import ToDos from './pages/ToDos';

function App() {
  const [page, setPage] = useState('login'); // login | register | todos
  const [userLogged, setUserLogged] = useState(!!localStorage.getItem('jwt'));

  const handleLoginSuccess = () => {
    setUserLogged(true);
    setPage('todos');
  };

  const handleLogout = () => {
    localStorage.removeItem('jwt');
    setUserLogged(false);
    setPage('login');
  };

  return (
    <div>
      {!userLogged && page === 'login' && <Login onLogin={handleLoginSuccess} goRegister={() => setPage('register')} />}
      {!userLogged && page === 'register' && <Register onRegister={() => setPage('login')} goLogin={() => setPage('login')} />}
      {userLogged && <ToDos onLogout={handleLogout} />}
    </div>
  );
}

export default App;
